using System.Collections.Generic;

namespace Adamantium.Fonts.Common
{
    public static class FeatureInfos
    {
        private static readonly string cvFeatureDescription = "A font may have stylistic-variant glyphs for one or more characters where the variations for one character are not systematically related to those for other characters. Or, a variation may exist for a character and its casing pair (or related pre-composed characters), but not be applicable to other unrelated characters. In some usage scenarios, it may be necessary to provide the application with control over glyph variations for different Unicode characters individually.";

        private static readonly string ssFeatureDescription =
            "In addition to, or instead of, stylistic alternatives of individual glyphs (see 'salt' feature), some fonts may contain sets of stylistic variant glyphs corresponding to portions of the character set, e.g. multiple variants for lowercase letters in a Latin font. Glyphs in stylistic sets may be designed to harmonise visually, interract in particular ways, or otherwise work together. Examples of fonts including stylistic sets are Zapfino Linotype and Adobe’s Poetica. Individual features numbered sequentially with the tag name convention 'ss01', 'ss02', 'ss03'… 'ss20' provide a mechanism for glyphs in these sets to be associated via GSUB lookup indices to default forms and to each other, and for users to select from available stylistic sets.";
        
        private static Dictionary<string, FeatureInfo> features;
        
        static FeatureInfos()
        {
            features = new Dictionary<string, FeatureInfo>();
            features["aalt"] = FeatureInfo.Create("aalt", "Access All Alternates", FeatureRegistration.Adobe, "This feature makes all variations of a selected character accessible. This serves several purposes: An application may not support the feature by which the desired glyph would normally be accessed; the user may need a glyph outside the context supported by the normal substitution, or the user may not know what feature produces the desired glyph. Since many-to-one substitutions are not covered, ligatures would not appear in this table unless they were variant forms of another ligature.");
            features["abvf"] = FeatureInfo.Create("abvf", "Above-base Forms", FeatureRegistration.Microsoft, "Substitutes the above-base form of a vowel. In particular, this feature is used for certain “split” vowels in Khmer or other Brahmi-derived scripts if the character has separate components that appear before and above a consonant, but the Unicode character for the vowel does not have a decomposition into separate characters for the two components.", overridesAllOtherFeatures: true);
            features["abvm"] = FeatureInfo.Create("abvm", "Above-base Mark Positioning", FeatureRegistration.Microsoft, "Positions marks above base glyphs.");
            features["abvs"] = FeatureInfo.Create("abvs", "Above-base Substitutions", FeatureRegistration.Microsoft, "Substitutes a ligature for a base glyph and mark that’s above it.");
            features["afrc"] = FeatureInfo.Create("afrc", "Alternative Fractions", FeatureRegistration.Microsoft, "Replaces figures separated by a slash with an alternative form.", overridesAllOtherFeatures: true);
            features["akhn"] = FeatureInfo.Create("akhn", "Akhand", FeatureRegistration.Microsoft, "Preferentially substitutes default glyphs for a sequence of characters with a ligature. This substitution is done irrespective of any characters that may precede or follow the sequence.", enableFeatures: new [] {"nukt", "akhn", "rphf", "rkrf", "pref", "blwf", "half", "pstf", "cjct"});
            features["blwf"] = FeatureInfo.Create("blwf", "Below-base Forms", FeatureRegistration.Microsoft, "Substitutes the below-base form of a consonant in conjuncts.", enableFeatures: new [] {"nukt", "akhn", "rphf", "rkrf", "pref", "blwf", "half", "pstf", "cjct"});
            features["blwm"] = FeatureInfo.Create("blwm", "Below-base Mark Positioning", FeatureRegistration.Microsoft, "Positions marks below base glyphs.");
            features["blws"] = FeatureInfo.Create("blws", "Below-base Substitutions", FeatureRegistration.Microsoft, "Produces ligatures that comprise of base glyph and below-base forms.", overridesAllOtherFeatures: true);
            features["calt"] = FeatureInfo.Create("calt", "Contextual Alternates", FeatureRegistration.Adobe, "In specified situations, replaces default glyphs with alternate forms which provide better joining behavior. Used in script typefaces which are designed to have some or all of their glyphs join.");
            features["case"] = FeatureInfo.Create("case", "Case-Sensitive Forms", FeatureRegistration.Adobe, "Shifts various punctuation marks up to a position that works better with all-capital sequences or sets of lining figures; also changes oldstyle figures to lining figures. By default, glyphs in a text face are designed to work with lowercase characters. Some characters should be shifted vertically to fit the higher visual center of all-capital or lining text. Also, lining figures are the same height (or close to it) as capitals, and fit much better with all-capital text.");
            features["ccmp"] = FeatureInfo.Create("ccmp", "Glyph Composition/Decomposition", FeatureRegistration.Microsoft, "To minimize the number of glyph alternates, it is sometimes desirable to decompose the default glyph for a character into two or more glyphs. Additionally, it may be preferable to compose default glyphs for two or more characters into a single glyph for better glyph processing. This feature permits such composition/decomposition. The feature should be processed as the first feature processed, and should be processed only when it is called.", FeatureState.On);
            features["cfar"] = FeatureInfo.Create("cfar", "Conjunct Form After Ro", FeatureRegistration.Microsoft, "Substitutes alternate below-base or post-base forms in Khmer script when occurring after conjoined Ro (“Coeng Ra”).");
            features["chws"] = FeatureInfo.Create("chws", "Contextual Half-width Spacing", FeatureRegistration.AdobeW3C, "Contextually re-spaces glyphs designed to be set on full-em widths, fitting them onto individual half-em horizontal widths, to approximate more sophisticated text layout, such as what is described in Requirements for Japanese Text Layout (JLREQ) or similar CJK text-layout specifications that expect half-width forms of characters whose default glyphs are full-width.* This feature differs from 'halt' in that the re-spacing is contextual. This feature may be invoked to get better fit for punctuation or symbol glyphs without disrupting the monospaced alignment, such as for UIs or terminal apps.", disableFeatures: new []{"fwid", "halt", "hwid", "palt", "pwid", "qwid", "twid", "kern"});
            features["cjct"] = FeatureInfo.Create("cjct", "Conjunct Forms", FeatureRegistration.Microsoft, "Produces conjunct forms of consonants in Indic scripts. This is similar to the Akhands feature, but is applied at a different sequential point in the process of shaping an Indic syllable.", enableFeatures: new []{"nukt", "akhn", "rphf", "rkrf", "pref", "blwf", "half", "pstf", "cjct"});
            features["clig"] = FeatureInfo.Create("clig", "Contextual Ligatures", FeatureRegistration.Adobe, "Replaces a sequence of glyphs with a single glyph which is preferred for typographic purposes. Unlike other ligature features, 'clig' specifies the context in which the ligature is recommended. This capability is important in some script designs and for swash ligatures.", FeatureState.On);
            features["cpct"] = FeatureInfo.Create("cpct", "Centered CJK Punctuation", FeatureRegistration.Adobe, "Centers specific punctuation marks for those fonts that do not include centered and non-centered forms.", disableFeatures: new []{"tnum", "fwid", "hwid", "halt", "palt", "twid"});
            features["cpsp"] = FeatureInfo.Create("cpsp", "Capital Spacing", FeatureRegistration.Adobe, "Globally adjusts inter-glyph spacing for all-capital text. Most typefaces contain capitals and lowercase characters, and the capitals are positioned to work with the lowercase. When capitals are used for words, they need more space between them for legibility and esthetics. This feature would not apply to monospaced designs. Of course the user may want to override this behavior in order to do more pronounced letterspacing for esthetic reasons.");
            features["cswh"] = FeatureInfo.Create("cswh", "Contextual Swash", FeatureRegistration.Adobe, "This feature replaces default character glyphs with corresponding swash glyphs in a specified context. Note that there may be more than one swash alternate for a given character.");
            features["curs"] = FeatureInfo.Create("curs", "Cursive Positioning", FeatureRegistration.Microsoft, "This feature is used to position adjacent glyphs for cursive connections.");
            
            features["cv01"] = FeatureInfo.Create("cv01", "Character Variant 1", FeatureRegistration.Microsoft, cvFeatureDescription);
